import React, { useState } from "react";
import albums from "../data/albums";

const ITEMS_PER_PAGE = 8;

export default function Music() {
  const [currentPage, setCurrentPage] = useState(1);

  const totalPages = Math.ceil(albums.length / ITEMS_PER_PAGE);
  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const currentAlbums = albums.slice(startIndex, startIndex + ITEMS_PER_PAGE);

  const goToNext = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };

  const goToPrev = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  return (
    <div className="max-w-5xl mx-auto px-6 py-12 text-black">
      <h1 className="text-4xl font-bold mb-8">Music I Like</h1>

      <div className="grid grid-cols-2 md:grid-cols-4 gap-6 mb-10">
        {currentAlbums.map((album, index) => (
          <div
            key={index}
            className="relative group overflow-hidden rounded shadow-lg"
          >
            <img
              src={album.img}
              alt={album.title}
              className="w-full aspect-square object-cover group-hover:opacity-30 transition duration-300"
            />
            <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition duration-300 bg-white bg-opacity-90 p-4 text-sm text-center">
              <p>{album.reaction}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Pagination Controls */}
      <div className="flex justify-center items-center space-x-4">
        <button
          onClick={goToPrev}
          disabled={currentPage === 1}
          className={`px-4 py-2 border rounded ${
            currentPage === 1 ? "text-gray-400 border-gray-300" : "hover:bg-gray-100"
          }`}
        >
          ← Previous
        </button>

        <span className="text-sm">
          Page {currentPage} of {totalPages}
        </span>

        <button
          onClick={goToNext}
          disabled={currentPage === totalPages}
          className={`px-4 py-2 border rounded ${
            currentPage === totalPages
              ? "text-gray-400 border-gray-300"
              : "hover:bg-gray-100"
          }`}
        >
          Next →
        </button>
      </div>
    </div>
  );
}
