// src/pages/BlogPost.js
import React from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { getPostById } from "../data/blog/index";
import ReactMarkdown from "react-markdown";

export default function BlogPost() {
  const { postId } = useParams();
  const navigate = useNavigate();
  const post = getPostById(postId);

  // If post doesn't exist, redirect to blog page
  React.useEffect(() => {
    if (!post) {
      navigate("/blog");
    }
  }, [post, navigate]);

  if (!post) return null;

  return (
    <div className="container mx-auto px-4 py-16">
      <div className="mb-8">
        <Link 
          to="/blog" 
          className="text-black hover:underline flex items-center"
        >
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            className="h-5 w-5 mr-1" 
            viewBox="0 0 20 20" 
            fill="currentColor"
          >
            <path 
              fillRule="evenodd" 
              d="M9.707 14.707a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 1.414L7.414 9H15a1 1 0 110 2H7.414l2.293 2.293a1 1 0 010 1.414z" 
              clipRule="evenodd" 
            />
          </svg>
          Back to All Posts
        </Link>
      </div>

      <article className="prose prose-lg max-w-none">
        <h1 className="text-3xl md:text-4xl font-bold text-black">{post.title}</h1>

        <div className="flex flex-col md:flex-row md:items-center text-gray-600 mb-8">
          <p className="text-sm md:text-base">{post.publication}</p>
          <span className="hidden md:inline mx-2">â€¢</span>
          <p className="text-sm md:text-base">
            {new Date(post.date).toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'long',
              day: 'numeric'
            })}
          </p>
        </div>

        <div className="content text-black">
          <ReactMarkdown>{post.content}</ReactMarkdown>
        </div>
      </article>
    </div>
  );
}
