// src/data/blog/index.js
import proteinFolding from './protein-folding-prediction.js';
import aiHealthcare from './ai-systems-healthcare.js';
import algorithmicPolicy from './algorithmic-decision-making-uk-policy.js';
import simpleInteractivePost from './simple-interactive-post.js';
import interactiveMachineLearning from './interactive-machine-learning-demo.js';

// Array of all blog posts
const allPosts = [
  simpleInteractivePost,
  interactiveMachineLearning,
  proteinFolding,
  aiHealthcare,
  algorithmicPolicy
];

// Sort posts by date (newest first)
const sortedPosts = [...allPosts].sort((a, b) => 
  new Date(b.date).getTime() - new Date(a.date).getTime()
);

// Helper function to get recent posts
export const getRecentPosts = (count = 3) => {
  return sortedPosts.slice(0, count);
};

// Helper function to get post by ID
export const getPostById = (id) => {
  return sortedPosts.find(post => post.id === id);
};

export default sortedPosts;
